mapper.update:
update auth_role_permission
set auth_role_id = #{authRoleId,jdbcType=INTEGER},
  auth_permission_id = #{authPermissionId,jdbcType=INTEGER},
  created_at = #{createdAt,jdbcType=TIMESTAMP},
  updated_at = #{updatedAt,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, auth_role_id, auth_permission_id, created_at, updated_at
from auth_role_permission
where id = #{id,jdbcType=INTEGER}
