mapper.update:
update auth_user_role
set auth_role_id = #{authRoleId,jdbcType=INTEGER},
  auth_user_id = #{authUserId,jdbcType=INTEGER},
  created_at = #{createdAt,jdbcType=TIMESTAMP},
  updated_at = #{updatedAt,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, auth_role_id, auth_user_id, created_at, updated_at
from auth_user_role
where id = #{id,jdbcType=INTEGER}
